public class Reverse_fn {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str= "Automation";
		System.out.println(str);
		StringBuilder str2= new StringBuilder();
		str2.append(str);
		str2= str2.reverse();
		System.out.println("Reverse : " + str2);

	}

}
